/*Table structure for table `edu_teacher` */

DROP TABLE IF EXISTS `edu_teacher`;

CREATE TABLE `edu_teacher` (
  `id` int(4) NOT NULL AUTO_INCREMENT COMMENT '讲师ID',
  `name` varchar(20) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '讲师姓名',
  `intro` varchar(500) CHARACTER SET utf8mb4 NOT NULL DEFAULT '' COMMENT '讲师简介',
  `career` varchar(500) CHARACTER SET utf8mb4 NOT NULL COMMENT '讲师资历,一句话说明讲师',
  `level` int(10) unsigned DEFAULT NULL COMMENT '头衔 1高级讲师 2首席讲师',
  `avatar` varchar(255) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '讲师头像',
  `sort` int(10) unsigned DEFAULT '0' COMMENT '排序',
